document.addEventListener('DOMContentLoaded', function() {
    var animation = lottie.loadAnimation({
        container: document.getElementById('zombie-animation'),
        renderer: 'svg',
        loop: true,
        autoplay: true,
        // Opción 1: Si el JSON está en la carpeta json dentro de animaciones
        // path: '../json/Frankenstein.json'
        
        // Opción 2: Usando ruta absoluta desde la raíz del proyecto
        path: '/zombie-plash/animaciones/json/Frankenstein.json'
        
        // Opción 3: Ruta relativa desde el HTML
        // path: '../animaciones/json/Frankenstein.json'
    });

    animation.addEventListener('data_failed', function(error) {
        console.error('No se pudo cargar el archivo JSON de la animación', error);
        // Agregar más detalles del error
        console.log('Ruta intentada:', animation.path);
    });

    animation.addEventListener('data_ready', function() {
        console.log('Animación cargada correctamente');
    });
});

