css.goteo.css:
.rain-container {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 1;
}

.raindrop {
    position: absolute;
    width: 20px;
    height: 80px;
    background: linear-gradient(transparent, rgb(255, 0, 0));
    animation: fall linear infinite;
}
.raindrop {
    width: 50px; /* Aumentar para gotas más gruesas */
    height: 100px; /* Aumentar para gotas más largas */
}
.raindrop {
    box-shadow: 0 0 5px rgb(255, 0, 0);
}
@keyframes fall {
    0% {
        transform: translateY(-100px);
        opacity: 0;
    }
    10% {
        opacity: 1;
    }
    100% {
        transform: translateY(100vh);
        opacity: 0.3;
    }
}

