css.unirseSala.css:
.container-fluid{
    
    margin: 0 !important;
    padding: 0 !important;
}
.imagen{
    background-image: url('../img/unirseSala.jpeg');
    background-size: cover; /* Hace que la imagen cubra toda la pantalla */
    background-position: center; /* Centra la imagen */
    background-repeat: no-repeat; /* Evita que la imagen se repita */
    background-attachment: fixed; /* Mantiene la imagen fija al hacer scroll */
    height: 100vh; /* Hace que ocupe la altura completa de la ventana */
    display: flex;
    align-items: center;
    justify-content: center;
    color: white; /* Cambia el color del texto para que sea visible */
    width: 100%;
    position: relative;
}
.cuadro{
    border: #e01616 solid 5px;
    width: 50%;
    height: 80%;
    background-color: rgba(14, 85, 87, 0.5); /* Azul con 50% de transparencia */
    border-radius: 40px;
    position: relative;
    filter: drop-shadow(0px 0px 12px rgb(7, 7, 6));
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: flex-start;
    padding-top: 40px;
    overflow: hidden;
}
.titulo{
    font-size: 40px;
    color:#c79bea;
    text-align: center;
    margin-bottom: 30px;
}
.cuadro::before{
    
        content: '';
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        border-radius: 40px;
        background-color: rgba(0, 0, 0, 0.3); /* Negro con 30% de transparencia */
        z-index: -1;
}
.cajita{
    width: 60%;
    display: flex;
    flex-direction: column;
    align-items: center;
}
.contra{
    text-align: center;
    color: white !important;
    font-size: 25px;
    margin-bottom: 10px;
}
.redondeo{
    border-radius: 22px;
    width: 100%;
    height: 40px;
    text-align: center;
    background-color: #d19797;
    font-size: 30px;
    filter: drop-shadow(0px 0px 12px rgb(182, 33, 6));
    margin-bottom: 20px;
}
.largo{
    display: flex;
    justify-content: center;
    width: 60%;
    margin-top: 30px;
    gap: 20px;
}
.cancelar{
    border-radius: 22px;
    background-color: red;
    filter: drop-shadow(0px 0px 12px rgb(255, 0, 0));
}
.Aceptar{
    border-radius: 22px;
    background-color: #e2b515;
    filter: drop-shadow(0px 0px 12px rgb(211, 156, 38));
}
/*espacion entre los dos botones*/

.btn{
    border: none;
    height: 100%;
    width: 100%;
    border-radius: 30px;
    padding: 10px 0;
    font-size: 16px;
}
.zombi1{
    height: 80%;
    width: 250px;
    top: -25%;
    left: 15%;
    position: absolute;
    z-index: 2;
}
.zombi2{
    height: 40%;
    width: 250px;
    bottom: 0;
    right: 20%;
    position: absolute;
    z-index: 2;
}
.altura{
    height: 100%;
    width: 100%;
    border: none;
}
@media (max-width: 768px) {
    .cuadro {
        width: 90%;
        height: auto;
        padding: 20px;
    }

    .titulo {
        font-size: 30px;
    }

    .contra {
        font-size: 20px;
    }

    .redondeo {
        font-size: 20px;
    }

    .largo {
        width: 100%;
    }

    .cancelar, .Aceptar {
        width: 40%;
    }

    .zombi1, .zombi2 {
        width: 150px;
    }

    .zombi1 {
        left: -50px;
    }

    .zombi2 {
        right: -50px;
    }
}

