.section:after {
	content:    '.';
	display:    block;
	height:     0;
	clear:      both;
	visibility: hidden;
}

.section {
	*zoom: 1;
}

.section .firstcolumn,
.section .firstcol {
	margin-left: 0;
}


/* Border on left hand side of a column. */
.border {
	padding-left: 7px;
	margin-left:  7px;
	border-left:  1px solid #eee;
}

/* Border with more whitespace, spans one column. */
