}

js.goteo.js:
// <!-- ayudame a elaborar un efecto de goteo para colocarlo de fondo para mi proyecto -->
function createRaindrop() {
    const rainContainer = document.getElementById('rainContainer');
    const raindrop = document.createElement('div');
    raindrop.classList.add('raindrop');
    
    // Posición aleatoria en X
    const posX = Math.random() * window.innerWidth;
    raindrop.style.left = posX + 'px';
    
    // Duración aleatoria de la animación
    const duration = Math.random() * 1 + 0.5; // Entre 0.5 y 1.5 segundos
    raindrop.style.animationDuration = duration + 's';
    
    rainContainer.appendChild(raindrop);
    
    // Eliminar la gota después de que termine la animación
    setTimeout(() => {
        raindrop.remove();
    }, duration * 1000);
}

// Crear gotas cada cierto intervalo
function startRain() {
    setInterval(createRaindrop, 100); // Crea una gota cada 100ms
}

const duration = Math.random() * 1 + 0.5; // Aumenta los números para gotas más lentas
// Iniciar el efecto cuando se carga la página
document.addEventListener('DOMContentLoaded', startRain);

js.jugadoresSala.js:
document.addEventListener('DOMContentLoaded', function() {
    const datosSala = JSON.parse(localStorage.getItem('datosSala'));
    console.log('Datos de la sala:', datosSala); // Para depuración

    if (datosSala) {
        document.getElementById('idSala').textContent = datosSala.id_sala;
        document.getElementById('jugadoresConectados').textContent = datosSala.jugadores_conectados + '/' + datosSala.max_jugadores;
        document.getElementById('contraseñaSala').textContent = datosSala.contraseña_sala;

        function actualizarJugadores() {
            fetch('../php/obtenerJugadoresSala.php?id_sala=' + datosSala.id_sala)
                .then(response => response.json())
                .then(jugadores => {
                    console.log("Jugadores recibidos:", jugadores);
                    const contenedorJugadores = document.getElementById('contenedorJugadores');
                    contenedorJugadores.innerHTML = ''; // Limpiar el contenedor

                    const totalFilas = Math.ceil(datosSala.max_jugadores / 5);

                    for (let i = 0; i < totalFilas; i++) {
                        const filaJugadores = document.createElement('div');
                        filaJugadores.className = 'fila-jugadores';

                        const filaCirculos = document.createElement('div');
                        filaCirculos.className = 'row1';
                        filaCirculos.style.backgroundImage = "url('../img/huellas2.jpg')";
                        filaCirculos.style.backgroundSize = "cover";
                        filaCirculos.style.backgroundPosition = "center";
                        
                        const filaNombres = document.createElement('div');
                        filaNombres.className = 'row2';
                        filaNombres.style.backgroundImage = "url('../img/huellas2.jpg')";
                        filaNombres.style.backgroundSize = "cover";
                        filaNombres.style.backgroundPosition = "center";

                        for (let j = 0; j < 5; j++) {
                            const index = i * 5 + j;
                            const colCirculo = document.createElement('div');
                            colCirculo.className = 'col2';
                            const circulo = document.createElement('div');
                            circulo.className = 'circulo';
                            colCirculo.appendChild(circulo);
                            filaCirculos.appendChild(colCirculo);

                            const colNombre = document.createElement('div');
                            colNombre.className = 'col2';
                            
                            if (index < jugadores.length) {
                                circulo.style.backgroundColor = '#00ff00';
                                colNombre.textContent = jugadores[index].nombre_jugador;
                            } else {
                                circulo.style.backgroundColor = '#ff0000';
                                colNombre.textContent = 'Esperando jugador...';
                            }
                            
                            filaNombres.appendChild(colNombre);
                        }

                        filaJugadores.appendChild(filaCirculos);
                        filaJugadores.appendChild(filaNombres);
                        contenedorJugadores.appendChild(filaJugadores);
                    }

                    document.getElementById('jugadoresConectados').textContent = jugadores.length + '/' + datosSala.max_jugadores;
                })
                .catch(error => {
                    console.error('Error al obtener jugadores:', error);
                });
        }

        actualizarJugadores();
        setInterval(actualizarJugadores, 5000);
    } else {
        console.error('No se encontraron datos de la sala en localStorage');
    }
});

function cancelarSala() {
    const datosSala = JSON.parse(localStorage.getItem('datosSala'));
    console.log('Datos de la sala:', datosSala); // Añade este log
    if (datosSala && datosSala.id_sala) {
        if (confirm('¿Estás seguro de que quieres cancelar la sala?')) {
            fetch('../php/cancelarSala.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ id_sala: datosSala.id_sala })
            })
            .then(response => response.json())
            .then(data => {
                if (data.success) {
                    alert('La sala ha sido cancelada exitosamente.');
                    localStorage.removeItem('datosSala');
                    window.location.href = 'inicio.php'; // Redirige al usuario a la página de inicio
                } else {
                    alert('Error al cancelar la sala: ' + data.message);
                }
            })
            .catch(error => {
                console.error('Error:', error);
                alert('Ocurrió un error al intentar cancelar la sala.');
            });
        }
    } else {
        alert('No se encontraron datos de la sala.');
    }
}

js.musica.js:
// Esperamos a que el DOM se cargue completamente
document.addEventListener("DOMContentLoaded", function () {
  let audio = document.getElementById("miAudio");

  // Al cargar la página, intentar reproducir el audio
  audio.play().catch(function (error) {
      console.log("Error al reproducir audio: " + error);
  });

  // Guardar el tiempo actual del audio antes de salir de la página
  window.addEventListener("beforeunload", function () {
      localStorage.setItem("audioTime", audio.currentTime);
  });

  // Al cargar la página, continuar desde el tiempo guardado
  window.onload = function () {
      var savedTime = localStorage.getItem("audioTime");
      if (savedTime !== null) {
          audio.currentTime = savedTime; // Continuar desde el tiempo guardado
      }
  };
});

js.unirseSala.js:
document.addEventListener('DOMContentLoaded', function() {
    const form = document.getElementById('unirseSalaForm');
    if (!form) {
        console.error('El formulario no se encontró en el DOM');
        return;
    }

    form.addEventListener('submit', function(event) {
        event.preventDefault();

        var formData = new FormData(this);

        fetch('../php/unirseSala.php', {
            method: 'POST',
            body: formData
        })
        .then(response => response.json())
        .then(data => {
            let mensaje = '';
            if (data.success) {
                localStorage.setItem('datosSala', JSON.stringify({
                    id_sala: data.id_sala,
                    contraseña_sala: data.contraseña_sala,
                    jugadores_conectados: data.jugadores_conectados,
                    max_jugadores: data.max_jugadores
                }));
                mensaje = '¡Te has unido a la sala con éxito!';
                // Redirigir a la página de jugadores en sala
                window.location.href = 'jugadoresSala.html';
            } else {
                if (data.error === 'foreign_key_constraint') {
                    mensaje = 'Error: El jugador no está registrado correctamente. Por favor, asegúrate de estar registrado antes de unirte a una sala.';
                } else {
                    mensaje = 'Error al unirse a la sala: ' + data.message;
                }
            }
            
            mostrarMensaje(mensaje);
        })
        .catch(error => {
            console.error('Error:', error);
            mostrarMensaje('Error al conectar con el servidor');
        });
    });
});

function mostrarMensaje(mensaje) {
    console.log(mensaje); // Siempre mostramos el mensaje en la consola

    // Intentamos actualizar el elemento HTML si existe
    const mensajeElement = document.getElementById('mensajeUnirse');
    if (mensajeElement) {
        mensajeElement.textContent = mensaje;
    }

    // Mostramos una alerta
    alert(mensaje);
}

js.validar_campos_registro.js:
document.getElementById('formulario').addEventListener('submit', function(e) {
    e.preventDefault();

    // Limpiar mensajes de error previos
    document.querySelectorAll('.error').forEach(function(el) {
        el.textContent = '';
    });

    // Verificar que las contraseñas coincidan
    var password = document.getElementById('contraseña').value;
    var confirmPassword = document.getElementById('confirmar_contraseña').value;

    if (password !== confirmPassword) {
        document.getElementById('confirmar_ContraseñaError').textContent = 'Las contraseñas no coinciden';
        return; // Detener el envío del formulario
    }

    // Realizar la validación mediante AJAX
    var formData = new FormData(this);

    fetch('../php/registro.php', {
        method: 'POST',
        body: formData
    })
    .then(response => response.json())
    .then(data => {
        console.log(data);
        if (data.success) {
            alert('Registro exitoso');
            window.location.href = '../html/login.php';
        } else {
            // Mostrar errores específicos
            if (data.errors.nombre) {
                document.getElementById('nombreError').textContent = data.errors.nombre;
            }
            if (data.errors.correo) {
                document.getElementById('correoError').textContent = data.errors.correo;
            }
            if (data.errors.contraseña) {
                document.getElementById('contraseñaError').textContent = data.errors.contraseña;
            }
            if (data.errors.confirmar_contraseña) {
                document.getElementById('confirmar_ContraseñaError').textContent = data.errors.confirmar_contraseña;
            }
        }
    })
    .catch(error => {
        console.error('Error:', error);
    });
});

js.validar_inicio_sesion.js:
document.getElementById('formulario').addEventListener('submit', function(e) {
    e.preventDefault();

    // Limpiar mensajes de error previos
    document.querySelectorAll('.error').forEach(function(el) {
        el.textContent = '';
    });

    var formData = new FormData(this);

    fetch('../php/inicio_sesion.php', {
        method: 'POST',
        body: formData,
        credentials: 'include' // Asegura que las cookies de sesión se envíen
    })
    .then(response => response.json())
    .then(data => {
        console.log('Respuesta completa del servidor:', data);
        if (data.success) {
            console.log('Inicio de sesión exitoso:', data.session_info);
            alert('Inicio de sesión exitoso. Redirigiendo...');
            window.location.href = '../html/inicio.php'; // Asegúrate de que esta ruta sea correcta
        } else {
            // Mostrar error general
            if (data.errors && data.errors.general) {
                document.getElementById('generalError').textContent = data.errors.general;
            } else {
                document.getElementById('generalError').textContent = 'Ocurrió un error desconocido';
            }
        }
        if (data.debug) {
            console.log('Información de depuración:', data.debug);
        }
    })
    .catch(error => {
        console.error('Error:', error);
        document.getElementById('generalError').textContent = 'Error de conexión con el servidor';
    });
});
