// URL del archivo JSON con la lista de jugadores
const url = 'jugadores.json';

// Función para crear una casilla de jugador
function crearJugador(jugador) {
    const contenedorInfoJugado = document.createElement('div');
    contenedorInfoJugado.classList.add('contenedor-info-jugado');

    const columna = document.createElement('div');
    columna.classList.add('columna');

    const usuario = document.createElement('div');
    usuario.classList.add('usuario');

    const img = document.createElement('img');
    img.src = jugador.foto;
    img.alt = jugador.nombre;
    usuario.appendChild(img);

    columna.appendChild(usuario);

    const columna1 = document.createElement('div');
    columna1.classList.add('columna1');

    const nombre = document.createElement('strong');
    nombre.textContent = jugador.nombre;
    columna1.appendChild(nombre);

    contenedorInfoJugado.appendChild(columna);
    contenedorInfoJugado.appendChild(columna1);

    return contenedorInfoJugado;
}

// Función para obtener la lista de jugadores desde el archivo JSON
function obtenerJugadores() {
    fetch(url)
        .then(response => response.json())
        .then(data => {
            const jugadores = data.jugadores;
            const contenedor = document.querySelector('.filass');

            jugadores.forEach(jugador => {
                const casilla = crearJugador(jugador);
                contenedor.appendChild(casilla);
            });
        })
        .catch(error => console.error('Error:', error));
}

// Llamar a la función para obtener la lista de jugadores
obtenerJugadores();

